import React, {
  useState,
  useContext
} from "react";
import "./CandidateList.css";
import AAP from "../images/aap.png";
import Congress from "../images/Congress.jpg";
import Nota from "../images/Nota.jpg";
import {
  UserContext
} from "../App";
import {
  ToastContainer,
  toast
} from "react-toastify";
import firebase from "../firebase";
import swal from "sweetalert";
import {
  useHistory
} from "react-router-dom";

function CandidateList() {
  const {
    state,
    dispatch
  } = useContext(UserContext);
  const history = useHistory();
  const [disable, setDisable] = useState(false);
  useEffect(() => {
        const user = JSON.parse(localStorage.getItem("user"));
        uId = user.uId;
        fetch('http://localhost:4001/transaction/broadcast', {

            method: "post",

            headers: {
              "Content-Type": "application/json",

            },
            body: JSON.stringify({
              uid: uId

            }),

          })
          .then((res) => res.json())
          .then((data) => {
            if (data.error) {
              toast.error(data.error, {
                position: "top-center",
              });
              setDisable(true);
            }})
         },[]);

        const postData = (candidateName) => {
          //console.log(candidateName);
          const user = JSON.parse(localStorage.getItem("user"));

          swal({
            content: {
              element: "input",
              attributes: {
                placeholder: "Enter your password",
                type: "password",
              },
            },
            closeOnEsc: false,
            closeOnClickOutside: false,
          }).then((value) => {
            if (!value) {
              // toast.warning("Please enter password", {
              //   position: "top-center",
              // });
              return;
            }
            //console.log(state);

            fetch("/verify-password", {
                method: "post",
                headers: {
                  "Content-Type": "application/json",
                  Authorization: "Bearer " + localStorage.getItem("jwt"),
                },
                body: JSON.stringify({
                  value,
                  phoneNo: state.phoneNo,
                }),
              })
              .then((res) => res.json())
              .then((data) => {
                // console.log(data);
                if (data.error) {
                  toast.error(data.error, {
                    position: "top-center",
                  });
                  return;
                } else {
                  // toast.success(data.phone, {
                  //   position: "top-center",
                  // });
                  firebase.auth().settings.appVerificationDisabledForTesting = true;

                  const recaptcha = new firebase.auth.RecaptchaVerifier(
                    "recaptcha-container"
                  );
                  // console.log(recaptcha, "<<------");
                  //const number = "+91" + data.phone;
                  const number = "+911234567890";
                  var n1 = number.substr(0, 5);
                  var n2 = number.substr(10);
                  var n = n1 + "*****" + n2;
                  // console.log("++++++++>>>>", number);
                  firebase
                    .auth()
                    .signInWithPhoneNumber(number, recaptcha)
                    .then((e) => {
                      swal(
                        "Enter 6 digit OTP sent to your registered Mobile No. " + n, {
                          content: "input",
                          closeOnEsc: false,
                          closeOnClickOutside: false,
                        }
                      ).then((value) => {
                        if (!value) {
                          toast.error("*OTP* does not match!", {
                            position: "top-center",
                          });
                          window.setTimeout(() => {
                            history.go(0);
                          }, 2000);
                          return;
                        }
                        e.confirm(value)
                          .then((res) => {
                            //console.log("===>>>", res);


                            fetch("/send-mail", {
                              method: "post",
                              headers: {
                                "Content-Type": "application/json",
                                Authorization: "Bearer " + localStorage.getItem("jwt"),
                              },
                              body: JSON.stringify({
                                email: user.email,
                                uid: user.uId,
                                candidateName,
                              }),
                            });
                            //dispatch({ type: "VOTE", payload: { voted: true } });
                            // console.log(state);

                            const {
                              uId,
                              city,
                              gender,
                              age
                            } = user;

                            const receiver = candidateName;
                            console.log(uId);
                            fetch('http://localhost:4001/transaction/broadcast', {

                                method: "post",

                                headers: {
                                  "Content-Type": "application/json",

                                },
                                body: JSON.stringify({
                                  uid: uId,
                                  receiver: candidateName,
                                  location: city,
                                  age: age,
                                  gender: gender

                                }),

                              })
                              .then((res) => res.json())
                              .then((data) => {
                                if (data.error) {
                                  toast.error(data.error, {
                                    position: "top-center",
                                  });

                                  return;
                                } else {
                                  console.log(data);
                                  swal(
                                    "ðŸŽ‰âœ¨",
                                    "Voted to " +
                                    candidateName +
                                    " successully. Check your email",
                                    "success"
                                  );
                                  window.setTimeout(() => {
                                    history.push("/dashboard");
                                  }, 1700);
                                }
                              }).catch((err) => {
                                console.log(err)
                              });



                          })
                          .catch((err) => {
                            toast.error("*OTP* does not match!", {
                              position: "top-center",
                            });
                            window.setTimeout(() => {
                              history.go(0);
                            }, 2000);
                            return;
                          });
                      });
                    })
                    .catch((err) => {
                      toast.error("*Mobile No.* is Invalid!", {
                        position: "top-center",
                      });
                      console.log(err);
                      window.setTimeout(() => {
                        history.go(0);
                      }, 2000);
                      return;
                    });
                }
              })
              .catch((err) => {
                console.log(err);
              });
          });
          //console.log("======>>>> ", state);
          //console.log("======>>>> ", state.voted);
        };

        return ( <
          >
          <
